import { Component } from '@angular/core';
import { FormGroup, FormControl, FormGroupDirective, FormBuilder, FormArray } from '@angular/forms'

@Component({
  selector: 'app-workouts',
  templateUrl: './workouts.component.html',
  styleUrls: ['./workouts.component.css']
})

export class WorkoutsComponent {
  constructor(private fb: FormBuilder) {}

  recordWorkout = this.fb.group({
    date:new FormControl(''),
    name:new FormControl(''),
    sets: this.fb.array([]),
  })

  get setsFieldFormArray(): any {
    return this.addWorkout.get('sets') as FormArray
  }

  addWorkout() {
    console.log(this.recordWorkout.value);
  }
}
