import { Component } from '@angular/core';
import { FormGroup, FormControl, FormGroupDirective, FormBuilder, FormArray, Form } from '@angular/forms'

@Component({
  selector: 'app-workouts',
  templateUrl: './workouts.component.html',
  styleUrls: ['./workouts.component.css']
})

export class WorkoutsComponent {
  constructor(private fb: FormBuilder) {}

  form = this.fb.group({
    date:new FormControl(''),
    name:new FormControl(''),
    sets: this.fb.array([]),
  })

  get setsFieldFormArray(): any {
    return this.form.get('sets') as FormArray
  }

  sets(): any {
    return this.fb.group({
      reps: this.fb.control(''),
    })
  }

  addControl(): void {
    this.setsFieldFormArray.push(this.sets());
  }

  remove(i: number): void {
    this.setsFieldFormArray.removeAt(i);
  }

  addWorkout() {
    console.log(this.form.value);
  }
}
<form [formGroup]="form">
	<div class="input-field">
		<input type="date" name="date" id="date" formControlName="date">
	</div>
	<div class="input-field">
		<input type="text" name="name" id="name" formControlName="name">
	</div>
	<div *ngFor="let control of  setsFieldFormArray.controls ;let i = index;" formArrayName='sets' >
        <div [formGroupName]="i" >
            <div  class="input-field" >
            <input type="text"
                placeholder="Reps" formControlName="reps" >
            </div>
            <button class="button" type="button" (click)="remove(i)" >Remove</button>
        </div>
    </div>
    <button  class="button" (click)="addControl()" > Add New Set</button>
  <button class="button" type="submit" (click)="addWorkout()" [disabled]="form.invalid">
    Login
  </button>

	<button type="submit">submit</button>
</form>
